export function assertIsArray(data, message) {
    if (Object.prototype.toString.call(data) !== '[object Array]') {
        throw new Error(message);
    }
}
export function getValues(data) {
    return Object.keys(data).map(key => data[key]);
}
export function getKeys(data) {
    return Object.keys(data);
}
export function getEntries(data) {
    return Object.keys(data).map(key => [key, data[key]]);
}
export function normalizeFileName(fileName, extension) {
    const suffix = '.' + extension;
    const extensionPattern = new RegExp(`(\\${extension})?$`);
    return fileName.replace(/\s+/, '_').replace(extensionPattern, suffix);
}
